-- Query to get the earliest creation time\nSELECT MIN(action_logs.time_added) AS created_time, article.number AS articleNum\nFROM action_logs\nJOIN article ON action_logs.article_num = article.number\nWHERE action_logs.message LIKE '%added%'\nGROUP BY article.number;\n\n-- Query to get the latest modification time\nSELECT MAX(action_logs.time_added) AS modified_time, article.number AS articleNum\nFROM action_logs\nJOIN article ON action_logs.article_num = article.number\nWHERE action_logs.message LIKE '%updated%'\nGROUP BY article.number;