MERGE INTO ReportDB.dbo.YourReportTable AS target\nUSING (SELECT * FROM WarehouseDB.dbo.YourWarehouseTable WHERE DateColumn >= DATEADD(MONTH, -3, GETDATE())) AS source\nON (target.PrimaryKey = source.PrimaryKey)\nWHEN MATCHED THEN\n    UPDATE SET target.Column1 = source.Column1, target.Column2 = source.Column2\nWHEN NOT MATCHED BY TARGET THEN\n    INSERT (PrimaryKey, Column1, Column2) VALUES (source.PrimaryKey, source.Column1, source.Column2)\nWHEN NOT MATCHED BY SOURCE AND target.DateColumn < DATEADD(MONTH, -3, GETDATE()) THEN\n    DELETE;