DECLARE @TableName NVARCHAR(128) = 'DelMe';\nDECLARE @SQL NVARCHAR(MAX) = '';\n\n-- Find all foreign key constraints referencing the specified table\nSELECT @SQL += 'DELETE FROM [' + FKTable + '] WHERE [' + FKColumn + '] IN (SELECT [ID] FROM [' + @TableName + ']);' + CHAR(13)\nFROM (\n    SELECT \n        fk.name AS FKName,\n        tp.name AS FKTable,\n        c.name AS FKColumn\n    FROM \n        sys.foreign_keys AS fk\n        INNER JOIN sys.foreign_key_columns AS fkc ON fk.object_id = fkc.constraint_object_id\n        INNER JOIN sys.columns AS c ON fkc.parent_object_id = c.object_id AND fkc.parent_column_id = c.column_id\n        INNER JOIN sys.tables AS tp ON fkc.parent_object_id = tp.object_id\n        INNER JOIN sys.tables AS tr ON fkc.referenced_object_id = tr.object_id\n    WHERE \n        tr.name = @TableName\n) AS FKInfo;\n\n-- Add the final delete statement for the main table\nSET @SQL += 'DELETE FROM [' + @TableName + '] WHERE [ID] = @ID;';\n\n-- Print the generated SQL script\nPRINT @SQL;