CREATE PROCEDURE GetPagedResults\n    @PageNumber INT,\n    @PageSize INT\nAS\nBEGIN\n    WITH OrderedResults AS\n    (\n        SELECT \n            *, \n            ROW_NUMBER() OVER (ORDER BY YourPrimaryKeyColumn) AS RowNumber\n        FROM \n            YourTable\n    )\n    SELECT \n        *\n    FROM \n        OrderedResults\n    WHERE \n        RowNumber BETWEEN (@PageNumber-1)*@PageSize+1 AND @PageNumber*@PageSize\n    ORDER BY \n        RowNumber;\nEND;