-- Sample table structure\nCREATE TABLE items (\n    id INT PRIMARY KEY,\n    name VARCHAR(100),\n    weight INT\n);\n\n-- Sample data\nINSERT INTO items (id, name, weight) VALUES\n(1, 'Item 1', 10),\n(2, 'Item 2', 20),\n(3, 'Item 3', 30),\n(4, 'Item 4', 40);\n\n-- Query to select a random item based on weight\nSELECT id, name \nFROM items \nORDER BY RAND() * weight DESC \nLIMIT 1;