-- Trigger for insert\nCREATE TRIGGER after_books_insert\nAFTER INSERT ON books\nFOR EACH ROW\nBEGIN\n    INSERT INTO books_audit (id, name, operation)\n    VALUES (NEW.id, NEW.name, 'I');\nEND;\n\n-- Trigger for delete\nCREATE TRIGGER after_books_delete\nAFTER DELETE ON books\nFOR EACH ROW\nBEGIN\n    INSERT INTO books_audit (id, name, operation)\n    VALUES (OLD.id, OLD.name, 'D');\nEND;