DECLARE @OrderByColumn NVARCHAR(50)\nDECLARE @OrderDirection INT\n\nSET @OrderByColumn = 'AddedDate'\nSET @OrderDirection = 1 -- 1 for ASC, -1 for DESC\n\nSELECT *\nFROM YourTable\nORDER BY \n    CASE \n        WHEN @OrderByColumn = 'AddedDate' AND @OrderDirection = 1 THEN CONVERT(varchar(50), AddedDate)\n        WHEN @OrderByColumn = 'Visible' AND @OrderDirection = 1 THEN CONVERT(varchar(2), Visible)\n        WHEN @OrderByColumn = 'AddedBy' AND @OrderDirection = 1 THEN AddedBy\n        WHEN @OrderByColumn = 'Title' AND @OrderDirection = 1 THEN Title\n\n        WHEN @OrderByColumn = 'AddedDate' AND @OrderDirection = -1 THEN CONVERT(varchar(50), AddedDate) * -1\n        WHEN @OrderByColumn = 'Visible' AND @OrderDirection = -1 THEN CONVERT(varchar(2), Visible) * -1\n        WHEN @OrderByColumn = 'AddedBy' AND @OrderDirection = -1 THEN AddedBy\n        WHEN @OrderByColumn = 'Title' AND @OrderDirection = -1 THEN Title\n    END