SELECT \n    n.nid, \n    n.type, \n    n.title, \n    nr.body, \n    nr.teaser, \n    FROM_UNIXTIME(e.event_start) AS start_date, \n    FROM_UNIXTIME(e.event_end) AS end_date\nFROM node n\nLEFT JOIN event e ON n.nid = e.nid\nLEFT JOIN node_revisions nr ON nr.nid = e.nid\nWHERE n.type = 'event'\nAND FROM_UNIXTIME(e.event_start) >= DATE_SUB(CURDATE(), INTERVAL WEEKDAY(CURDATE()) + 1 DAY)\nAND FROM_UNIXTIME(e.event_start) < DATE_ADD(DATE_SUB(CURDATE(), INTERVAL WEEKDAY(CURDATE()) + 1 DAY), INTERVAL 7 DAY)\nORDER BY n.created DESC\nLIMIT 5;