-- Step 1: Add a new column with the correct data type\nALTER TABLE YourTableName ADD BusinessDateTemp date;\n\n-- Step 2: Update the new column with the converted values\nUPDATE YourTableName\nSET BusinessDateTemp = CAST(BusinessDate AS date);\n\n-- Step 3: Drop the old column\nALTER TABLE YourTableName DROP COLUMN BusinessDate;\n\n-- Step 4: Rename the new column to the original column name\nEXEC sp_rename 'YourTableName.BusinessDateTemp', 'BusinessDate', 'COLUMN';