USE master;\nGO\n\n-- Create a new backup job\nEXEC msdb.dbo.sp_add_job\n    @job_name = N'DatabaseBackup';\n\n-- Add a job step to perform the backup\nEXEC msdb.dbo.sp_add_jobstep\n    @job_name = N'DatabaseBackup',\n    @step_name = N'BackupStep',\n    @subsystem = N'TSQL',\n    @command = N'BACKUP DATABASE [YourDatabaseName] \n                 TO DISK = N''C:\Backup\YourDatabaseName.bak'' \n                 WITH NOFORMAT, NOINIT, \n                 NAME = N''YourDatabaseName-Full Database Backup'', \n                 SKIP, NOREWIND, NOUNLOAD, STATS = 10';\n\n-- Schedule the job to run daily\nEXEC msdb.dbo.sp_add_jobschedule\n    @job_name = N'DatabaseBackup',\n    @name = N'DailyBackupSchedule',\n    @freq_type = 4,\n    @freq_interval = 1,\n    @active_start_time = 010000;\n\n-- Add the job to the SQL Server Agent\nEXEC msdb.dbo.sp_add_jobserver\n    @job_name = N'DatabaseBackup';\n\nGO