CREATE PROCEDURE ValidateDateParameter\n    @DateInput VARCHAR(8)\nAS\nBEGIN\n    -- Check if the input is exactly 8 characters long and numeric\n    IF LEN(@DateInput) = 8 AND ISNUMERIC(@DateInput) = 1\n    BEGIN\n        -- Try to convert the input to a date\n        DECLARE @Date DATE\n        BEGIN TRY\n            SET @Date = CONVERT(DATE, @DateInput, 112)\n            -- If conversion is successful, you can proceed with your logic\n            PRINT 'Valid date: ' + CONVERT(VARCHAR, @Date, 121)\n        END TRY\n        BEGIN CATCH\n            -- Handle conversion error\n            PRINT 'Invalid date format. Please use YYYYMMDD format.'\n        END CATCH\n    END\n    ELSE\n    BEGIN\n        PRINT 'Invalid date format. Please use YYYYMMDD format.'\n    END\nEND