CREATE FUNCTION dbo.YourFunction(@searchString NVARCHAR(MAX))\nRETURNS TABLE\nAS\nRETURN\n(\n    IF @searchString = ''\n    BEGIN\n        SELECT *\n        FROM YourTable\n    END\n    ELSE\n    BEGIN\n        SELECT\n            t.*, ct.RANK\n        FROM\n            YourTable AS t\n            INNER JOIN CONTAINSTABLE(YourTable, YourColumn, @searchString) AS ct\n            ON t.YourPrimaryKey = ct.[KEY]\n    END\n)