CREATE PROCEDURE [RetailAdmin].[TitleSearch] (\n        @isbn VARCHAR(50), \n        @author VARCHAR(50), \n        @title VARCHAR(50)\n    )\n    AS\n    BEGIN\n        SELECT \n            storeid = 0, \n            Author = '', \n            Title = '', \n            ISBN = '', \n            Imprint = '', \n            Edition = '', \n            Copyright = '', \n            stockonhand = 0\n        WHERE 1 = 0\n    END