CREATE TABLE Entity1 (\n    Entity1ID INT PRIMARY KEY,\n    -- other columns\n);\n\nCREATE TABLE Entity2 (\n    Entity2ID INT PRIMARY KEY,\n    -- other columns\n);\n\nCREATE TABLE Entity1_Entity2 (\n    Entity1ID INT,\n    Entity2ID INT,\n    ValidStart DATETIME,\n    ValidEnd DATETIME,\n    PRIMARY KEY (Entity1ID, Entity2ID, ValidStart),\n    FOREIGN KEY (Entity1ID) REFERENCES Entity1(Entity1ID),\n    FOREIGN KEY (Entity2ID) REFERENCES Entity2(Entity2ID)\n);