-- Using a join hint\nSELECT <Fields>\nFROM <Multiple Tables Joined>\n    LEFT HASH JOIN (\n        SELECT <Fields> \n        FROM <Multiple Tables Joined>\n    ) ON <Condition>;\n\n-- Using a table-valued function\nCREATE FUNCTION dbo.MySubQueryFunction()\nRETURNS @ResultTable TABLE (<Fields>)\nAS\nBEGIN\n    INSERT INTO @ResultTable\n    SELECT <Fields>\n    FROM <Multiple Tables Joined>;\n    \n    RETURN;\nEND;\n\n-- Using the table-valued function in the main query\nSELECT <Fields>\nFROM <Multiple Tables Joined>\n    LEFT JOIN dbo.MySubQueryFunction() ON <Condition>;