-- In the calling procedure\nCREATE PROCEDURE ProcCaller AS\nBEGIN\n    DECLARE @XmlData XML\n\n    -- Generate XML data\n    SET @XmlData = (\n        SELECT 1 AS ID, 'John Doe' AS Name\n        FOR XML PATH('Row'), ROOT('Root')\n    )\n\n    -- Call the other procedure with XML data\n    EXEC ProcCallee @XmlData\nEND\n\n-- In the called procedure\nCREATE PROCEDURE ProcCallee (@XmlData XML) AS\nBEGIN\n    -- Parse the XML data\n    SELECT\n        T.C.value('(ID)[1]', 'INT') AS ID,\n        T.C.value('(Name)[1]', 'NVARCHAR(50)') AS Name\n    FROM @XmlData.nodes('/Root/Row') AS T(C)\nEND