-- In the calling procedure\nCREATE PROCEDURE ProcCaller AS\nBEGIN\n    CREATE TABLE #TempTable (ID INT, Name NVARCHAR(50))\n\n    -- Insert the result of the called procedure into the temporary table\n    INSERT INTO #TempTable (ID, Name)\n    EXEC ProcCallee\n\n    -- Access the temporary table\n    SELECT * FROM #TempTable\n\n    -- Drop the temporary table if needed\n    DROP TABLE #TempTable\nEND\n\n-- In the called procedure\nCREATE PROCEDURE ProcCallee AS\nBEGIN\n    -- Return some data\n    SELECT 1 AS ID, 'John Doe' AS Name\n    UNION ALL\n    SELECT 2, 'Jane Smith'\nEND