-- In the calling procedure\nCREATE PROCEDURE ProcCaller AS\nBEGIN\n    DECLARE @TableVar TABLE (ID INT, Name NVARCHAR(50))\n\n    -- Populate the table variable\n    INSERT INTO @TableVar (ID, Name)\n    VALUES (1, 'John Doe'), (2, 'Jane Smith')\n\n    -- Call the other procedure with the table variable\n    EXEC ProcCallee @TableVar\nEND\n\n-- In the called procedure\nCREATE PROCEDURE ProcCallee (@TableVar TABLE (ID INT, Name NVARCHAR(50))) AS\nBEGIN\n    -- Access the table variable\n    SELECT * FROM @TableVar\nEND