CREATE TABLE row_count_table (\n        table_name VARCHAR2(100),\n        row_count NUMBER\n    );\n\n    CREATE OR REPLACE TRIGGER trigger_row_count\n    AFTER INSERT OR DELETE ON table_name\n    FOR EACH ROW\n    BEGIN\n        IF INSERTING THEN\n            UPDATE row_count_table\n            SET row_count = row_count + 1\n            WHERE table_name = 'table_name';\n        ELSIF DELETING THEN\n            UPDATE row_count_table\n            SET row_count = row_count - 1\n            WHERE table_name = 'table_name';\n        END IF;\n    END;