-- Example table\nCREATE TABLE BinaryData (\n    Id INT PRIMARY KEY,\n    Data VARBINARY(MAX)\n);\n\n-- Example data\nINSERT INTO BinaryData (Id, Data) VALUES (1, 0x01020304), (2, 0x00010203);\n\n-- Query to check if the value at a specific index (e.g., index 3) is 0\nDECLARE @Index INT = 3;\n\nSELECT \n    Id,\n    CASE \n        WHEN SUBSTRING(Data, @Index+1, 1) = 0x00 THEN 'Value is 0'\n        ELSE 'Value is not 0'\n    END AS CheckResult\nFROM BinaryData;