DELIMITER //\n\nCREATE TRIGGER after_user_update\nAFTER UPDATE ON central_db.users\nFOR EACH ROW\nBEGIN\n    UPDATE forum_db.users\n    SET username = NEW.username, email = NEW.email, password = NEW.password, created_at = NEW.created_at\n    WHERE id = NEW.id;\n\n    UPDATE news_db.users\n    SET username = NEW.username, email = NEW.email, password = NEW.password, created_at = NEW.created_at\n    WHERE id = NEW.id;\n\n    UPDATE calendar_db.users\n    SET username = NEW.username, email = NEW.email, password = NEW.password, created_at = NEW.created_at\n    WHERE id = NEW.id;\nEND//\n\nDELIMITER ;