CREATE TABLE forum_db.users (\n    id INT,\n    username VARCHAR(50),\n    email VARCHAR(100),\n    password VARCHAR(255),\n    created_at TIMESTAMP,\n    PRIMARY KEY (id)\n);\n\nCREATE TABLE news_db.users (\n    id INT,\n    username VARCHAR(50),\n    email VARCHAR(100),\n    password VARCHAR(255),\n    created_at TIMESTAMP,\n    PRIMARY KEY (id)\n);\n\nCREATE TABLE calendar_db.users (\n    id INT,\n    username VARCHAR(50),\n    email VARCHAR(100),\n    password VARCHAR(255),\n    created_at TIMESTAMP,\n    PRIMARY KEY (id)\n);