SELECT \n    P.FirstName, \n    P.LastName, \n    Count(A.attendance_date) AS CountOfattendance_date,\n    First(A.attendance_date) AS FirstOfattendance_date,\n    (SELECT COUNT(*)\n     FROM (SELECT DISTINCT attendance_date \n           FROM tblEventAttendance AS B\n           WHERE B.event_id = 8 \n             AND B.attendance_date >= Min(A.attendance_date))\n    ) AS total\nFROM \n    tblPeople AS P \n    INNER JOIN tblEventAttendance AS A \n    ON P.ID = A.people_id\nWHERE \n    A.event_id = 8\nGROUP BY \n    P.FirstName, \n    P.LastName\n;