BEGIN\n    IF HAS_PERMS_BY_NAME(DB_NAME(), 'DATABASE', 'BACKUP DATABASE') = 0\n       OR HAS_PERMS_BY_NAME(DB_NAME(), 'DATABASE', 'CREATE TABLE') = 0\n       OR HAS_PERMS_BY_NAME(DB_NAME(), 'DATABASE', 'CREATE PROCEDURE') = 0\n    BEGIN\n        RAISERROR('Insufficient permissions to perform one or more required actions. The script will now exit.', 16, 1);\n        RETURN;\n    END\nEND\n\n-- Continue with the rest of your setup script here