-- Example of range partitioning by year\nCREATE TABLE historical_transactions (\n    transaction_id INT,\n    data TEXT,\n    timestamp TIMESTAMP\n) PARTITION BY RANGE (EXTRACT(YEAR FROM timestamp));\n\nCREATE TABLE historical_transactions_2022 PARTITION OF historical_transactions \nFOR VALUES FROM (2022) TO (2023);\n\nCREATE TABLE historical_transactions_2023 PARTITION OF historical_transactions \nFOR VALUES FROM (2023) TO (2024);