-- Assuming active_transactions and historical_transactions tables are already created\n\n-- Create the trigger to handle insert/update/delete\nCREATE TRIGGER archive_transaction\nAFTER UPDATE ON active_transactions\nFOR EACH ROW\nBEGIN\n    -- Insert the old record into historical_transactions\n    INSERT INTO historical_transactions (transaction_id, data, timestamp)\n    VALUES (OLD.transaction_id, OLD.data, OLD.timestamp);\nEND;