SELECT blog.id, blog.title, blog.text, blog.user_id, users.username, users.email\nFROM blog\nJOIN users ON blog.user_id = users.id\nWHERE blog.user_id = {$user_id}\n\nUNION\n\nSELECT blog.id, blog.title, blog.text, blog.user_id, users.username, users.email\nFROM blog\nJOIN users ON blog.user_id = users.id\nJOIN relations ON relations.following_id = blog.user_id\nWHERE relations.follower_id = {$user_id}\nORDER BY blog.posted DESC;