CREATE OR REPLACE\nPACKAGE BODY TITAN_ENTITY AUTHID CURRENT_USER AS\n\n  PROCEDURE GETSCHEMAOBJECTS (RESULTS IN OUT T_CURSOR)\n  IS\n    V_CURSOR T_CURSOR;\n  BEGIN\n    OPEN V_CURSOR FOR\n       SELECT 'T' OBJECTTYPE, TABLE_NAME OBJECTNAME \n          FROM DBA_TABLES \n          WHERE OWNER = 'SONAR5'\n       UNION ALL\n       SELECT 'V' OBJECTTYPE, VIEW_NAME OBJECTNAME \n         FROM DBA_VIEWS \n         WHERE OWNER = 'SONAR5'\n       ORDER BY OBJECTNAME;\n    RESULTS := V_CURSOR;     \n\n  END GETSCHEMAOBJECTS;\n\nEND TITAN_ENTITY;