DECLARE @email_address VARCHAR(200) = 'test@example.com';\n\nIF @email_address LIKE '%_@_%_.__%' AND\n   CHARINDEX(' ', @email_address) = 0 AND\n   CHARINDEX('@', @email_address) = CHARINDEX('@', REVERSE(@email_address)) AND\n   CHARINDEX('.', @email_address, CHARINDEX('@', @email_address)) > CHARINDEX('@', @email_address)\nBEGIN\n    PRINT 'Valid email format'\nEND\nELSE\nBEGIN\n    PRINT 'Invalid email format'\nEND