WITH time_buckets AS (\n    SELECT\n        TRUNC(TO_DATE('2023-04-18 07:00:00', 'YYYY-MM-DD HH24:MI:SS') + (LEVEL - 1) / 1440, 'MI') AS time_bucket\n    FROM\n        dual\n    CONNECT BY\n        LEVEL <= 1440 -- Number of minutes in 24 hours\n),\njobs AS (\n    SELECT\n        job_id, \n        owner, \n        start_time, \n        end_time\n    FROM\n        your_table\n    WHERE\n        owner = 'Alice'\n        AND start_time < TO_DATE('2023-04-19 07:00:00', 'YYYY-MM-DD HH24:MI:SS')\n        AND end_time > TO_DATE('2023-04-18 07:00:00', 'YYYY-MM-DD HH24:MI:SS')\n)\nSELECT\n    tb.time_bucket,\n    COUNT(j.job_id) AS job_count\nFROM\n    time_buckets tb\nLEFT JOIN\n    jobs j\nON\n    tb.time_bucket BETWEEN j.start_time AND j.end_time\nGROUP BY\n    tb.time_bucket\nORDER BY\n    tb.time_bucket;