-- Create full-text index for video titles\nCREATE FULLTEXT INDEX idx_video_title ON videos(video_name);\n\n-- Create indexes for foreign keys\nCREATE INDEX idx_videotags_video_id ON videotags(video_id);\nCREATE INDEX idx_videotags_tag_id ON videotags(tag_id);\nCREATE INDEX idx_videoviews_video_id ON videoviews(video_id);\nCREATE INDEX idx_videoratings_video_id ON videoratings(video_id);