SELECT v.video_name, \n       (MATCH(v.video_name) AGAINST(:video_title IN NATURAL LANGUAGE MODE) * 0.3 +\n       (SELECT COUNT(*) FROM videotags vt WHERE vt.video_id = v.video_id AND vt.tag_id IN \n           (SELECT tag_id FROM videotags WHERE video_id = :current_video_id)) * 0.4 +\n       (SELECT COUNT(*) FROM videoviews vv WHERE vv.video_id = v.video_id) * 0.2 +\n       (SELECT AVG(rating) FROM videoratings vr WHERE vr.video_id = v.video_id) * 0.1) AS relevance_score\nFROM videos v\nWHERE v.video_id <> :current_video_id\nORDER BY relevance_score DESC\nLIMIT 5;