-- Create a temporary table to hold dictionary words\nCREATE TABLE #Dictionary (\n    Word NVARCHAR(50)\n);\n\n-- Insert some sample words into the dictionary table\nINSERT INTO #Dictionary (Word)\nVALUES ('apple'), ('banana'), ('cherry'), ('date'), ('elderberry'), ('fig'), ('grape'), ('honeydew');\n\n-- Function to generate a random integer\nCREATE FUNCTION dbo.RandomInt(@Min INT, @Max INT)\nRETURNS INT\nAS\nBEGIN\n    RETURN CAST((@Min + (RAND(CHECKSUM(NEWID())) * (@Max - @Min + 1))) AS INT);\nEND;\n\n-- Generate random paragraphs\nDECLARE @Paragraphs INT = 3; -- Number of paragraphs\nDECLARE @SentencesPerParagraph INT = 5; -- Number of sentences per paragraph\nDECLARE @WordsPerSentence INT = 8; -- Number of words per sentence\n\nDECLARE @Result NVARCHAR(MAX) = '';\nDECLARE @Paragraph NVARCHAR(MAX);\nDECLARE @Sentence NVARCHAR(MAX);\n\nWHILE @Paragraphs > 0\nBEGIN\n    SET @Paragraph = '';\n    DECLARE @Sentences INT = @SentencesPerParagraph;\n\n    WHILE @Sentences > 0\n    BEGIN\n        SET @Sentence = '';\n        DECLARE @Words INT = @WordsPerSentence;\n\n        WHILE @Words > 0\n        BEGIN\n            DECLARE @RandomIndex INT = dbo.RandomInt(1, (SELECT COUNT(*) FROM #Dictionary));\n            DECLARE @Word NVARCHAR(50) = (SELECT Word FROM #Dictionary WHERE Word IN (SELECT TOP 1 Word FROM #Dictionary ORDER BY NEWID()));\n            \n            SET @Sentence = @Sentence + ' ' + @Word;\n            SET @Words = @Words - 1;\n        END\n\n        SET @Paragraph = @Paragraph + '.' + @Sentence;\n        SET @Sentences = @Sentences - 1;\n    END\n\n    SET @Result = @Result + CHAR(13) + CHAR(10) + @Paragraph;\n    SET @Paragraphs = @Paragraphs - 1;\nEND\n\n-- Print the result\nPRINT @Result;\n\n-- Clean up\nDROP TABLE #Dictionary;\nDROP FUNCTION dbo.RandomInt;