-- Assume you have a table named TEST with a column ID\nCREATE TABLE TEST (ID INT);\n\n-- Declare a variable to hold the type of the column\nDECLARE @DataType NVARCHAR(128);\nDECLARE @Length INT;\n\n-- Retrieve the data type and length of the column\nSELECT @DataType = DATA_TYPE, @Length = CHARACTER_MAXIMUM_LENGTH\nFROM INFORMATION_SCHEMA.COLUMNS\nWHERE TABLE_NAME = 'TEST' AND COLUMN_NAME = 'ID';\n\n-- Declare a variable of the same type as the column\nDECLARE @myVar SQL_VARIANT;\n\n-- Set the variable to the type and length of the column dynamically\nIF @DataType = 'int'\n    SET @myVar = CAST(0 AS INT);\n-- Add more conditions for other data types as needed\n\n-- Use the variable in your logic\nBEGIN\n    -- Your logic here\nEND;