-- First set: ID and NAME match, ADDRESS does not match\nSELECT DISTINCT t1.*\nFROM table1 t1\nINNER JOIN table2 t2\n    ON t2.ID = t1.ID\n    AND t2.NAME = t1.NAME\n    AND t2.ADDRESS <> t1.ADDRESS;\n\n-- Second set: ID matches but NAME does not, exclude rows from the first set\nSELECT DISTINCT t1.*\nFROM table1 t1\nINNER JOIN table2 t2\n    ON t2.ID = t1.ID\n    AND t2.NAME <> t1.NAME\n    AND t2.ADDRESS <> t1.ADDRESS\nWHERE NOT EXISTS (\n    SELECT 1\n    FROM table2 t3\n    WHERE t3.ID = t1.ID\n    AND t3.NAME = t1.NAME\n    AND t3.ADDRESS <> t1.ADDRESS\n);\n\n-- Third set: ID does not match but NAME matches, exclude rows from the first and second sets\nSELECT DISTINCT t1.*\nFROM table1 t1\nINNER JOIN table2 t2\n    ON t2.ID <> t1.ID\n    AND t2.NAME = t1.NAME\n    AND t2.ADDRESS <> t1.ADDRESS\nWHERE NOT EXISTS (\n    SELECT 1\n    FROM table2 t3\n    WHERE t3.ID = t1.ID\n    AND t3.NAME = t1.NAME\n    AND t3.ADDRESS <> t1.ADDRESS\n)\nAND NOT EXISTS (\n    SELECT 1\n    FROM table2 t3\n    WHERE t3.ID = t1.ID\n    AND t3.NAME <> t1.NAME\n    AND t3.ADDRESS <> t1.ADDRESS\n);