-- Define the date range\nSET @start_date = '2023-01-01';\nSET @end_date = '2023-01-10';\n\n-- Create a sequence of dates within the specified range\nWITH RECURSIVE date_range AS (\n    SELECT @start_date AS date\n    UNION ALL\n    SELECT DATE_ADD(date, INTERVAL 1 DAY)\n    FROM date_range\n    WHERE date < @end_date\n)\nSELECT\n    COALESCE(AVG(ps.sales_price), 0) AS avg,\n    DATE_FORMAT(dr.date, '%m-%d-%Y') AS date\nFROM\n    date_range dr\nLEFT JOIN\n    products_sold ps ON DATE(ps.created_at) = dr.date AND ps.merchant_id = 1\nGROUP BY\n    dr.date\nORDER BY\n    dr.date;