-- History table\nCREATE TABLE MyTableHistory\n(\n    ID INT,\n    Value NVARCHAR(100),\n    ChangeDate DATETIME,\n    OperationType NVARCHAR(10)\n);\n\n-- Trigger for INSERT\nCREATE TRIGGER trg_MyTable_Insert\nON MyTable\nAFTER INSERT\nAS\nBEGIN\n    INSERT INTO MyTableHistory (ID, Value, ChangeDate, OperationType)\n    SELECT ID, Value, GETDATE(), 'INSERT'\n    FROM inserted;\nEND;\n\n-- Trigger for UPDATE\nCREATE TRIGGER trg_MyTable_Update\nON MyTable\nAFTER UPDATE\nAS\nBEGIN\n    INSERT INTO MyTableHistory (ID, Value, ChangeDate, OperationType)\n    SELECT ID, Value, GETDATE(), 'UPDATE'\n    FROM inserted;\nEND;\n\n-- Trigger for DELETE\nCREATE TRIGGER trg_MyTable_Delete\nON MyTable\nAFTER DELETE\nAS\nBEGIN\n    INSERT INTO MyTableHistory (ID, Value, ChangeDate, OperationType)\n    SELECT ID, Value, GETDATE(), 'DELETE'\n    FROM deleted;\nEND;