WITH RECURSIVE CategoryPath (id, name, parentID, path) AS (\n  -- Anchor member: select the starting category\n  SELECT id, name, parentID, name AS path\n  FROM categories\n  WHERE id = ?  -- Replace with the desired category ID\n\n  UNION ALL\n\n  -- Recursive member: join the categories table to itself\n  SELECT c.id, c.name, c.parentID, CONCAT(cp.path, ' -> ', c.name)\n  FROM categories c\n  JOIN CategoryPath cp ON c.id = cp.parentID\n)\n-- Select the final path, ordering by the length of the path to get the full breadcrumb trail\nSELECT path\nFROM CategoryPath\nORDER BY LENGTH(path) DESC\nLIMIT 1;