-- Step 1: Create a temporary table to store PIDMs\nCREATE TABLE #TempPIDMs (pidm INT);\n\n-- Step 2: Insert the results of the SQL Server query into the temporary table\nINSERT INTO #TempPIDMs (pidm)\nSELECT pidm\nFROM sql_server_table;\n\n-- Step 3: Use OPENQUERY to query the Oracle database with the PIDMs\nSELECT pidm\nFROM OPENQUERY(oracledb,\n'SELECT pidm\n FROM table\n WHERE pidm IN \n       (SELECT pidm FROM #TempPIDMs)')\n;\n\n-- Clean up the temporary table\nDROP TABLE #TempPIDMs;