-- Sample table creation\nCREATE TABLE SampleTable (\n    FieldA INT,\n    FieldB VARCHAR(50)\n);\n\n-- Insert a sample row\nINSERT INTO SampleTable (FieldA, FieldB) \nVALUES (1, 'Some Text');\n\n-- Query to display a single row as a single column\nSELECT 'FieldA' AS Header, CAST(FieldA AS VARCHAR(50)) AS Value\nFROM SampleTable\nUNION ALL\nSELECT 'FieldB' AS Header, FieldB AS Value\nFROM SampleTable;