CREATE PROCEDURE dbo.dmsInsertTestData\n    @ptestdata VarChar(50)\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    BEGIN TRY\n        INSERT INTO dbo.MyTestData (testdata) VALUES (@ptestdata);\n    END TRY\n    BEGIN CATCH\n        -- Handle the error or log it if necessary\n        IF ERROR_NUMBER() <> 2627 -- 2627 = Violation of PRIMARY KEY constraint\n        BEGIN\n            -- Re-raise the error if it's not a PK violation\n            THROW;\n        END\n    END CATCH;\n\n    RETURN 0;\nEND;