CREATE PROCEDURE SearchRecords\n    @Param1 NVARCHAR(50) = NULL,\n    @Param2 INT = NULL,\n    @Param3 DATE = NULL,\n    -- Add all other parameters here\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    DECLARE @SQL NVARCHAR(MAX);\n    SET @SQL = 'SELECT * FROM YourTable WHERE 1 = 1';\n\n    IF @Param1 IS NOT NULL\n        SET @SQL = @SQL + ' AND Column1 = @Param1';\n    IF @Param2 IS NOT NULL\n        SET @SQL = @SQL + ' AND Column2 = @Param2';\n    IF @Param3 IS NOT NULL\n        SET @SQL = @SQL + ' AND Column3 = @Param3';\n    -- Add all other conditions here\n\n    EXEC sp_executesql @SQL,\n        N'@Param1 NVARCHAR(50), @Param2 INT, @Param3 DATE',\n        @Param1, @Param2, @Param3;\nEND