DELIMITER //\n\nCREATE TRIGGER limit_downloads\nAFTER INSERT ON downloads\nFOR EACH ROW\nBEGIN\n    DECLARE row_count INT;\n    \n    -- Count the number of rows in the table\n    SELECT COUNT(*) INTO row_count FROM downloads;\n    \n    -- If there are more than 10 rows, delete the oldest one\n    IF row_count > 10 THEN\n        DELETE FROM downloads\n        ORDER BY download_date ASC\n        LIMIT 1;\n    END IF;\nEND;\n//\n\nDELIMITER ;