SELECT\n  MAX(CASE WHEN ID = 1 THEN ContactName END) AS ContactName1,\n  MAX(CASE WHEN ID = 1 THEN Designation END) AS Designation1,\n  MAX(CASE WHEN ID = 2 THEN ContactName END) AS ContactName2,\n  MAX(CASE WHEN ID = 2 THEN Designation END) AS Designation2,\n  MAX(CASE WHEN ID = 3 THEN ContactName END) AS ContactName3,\n  MAX(CASE WHEN ID = 3 THEN Designation END) AS Designation3,\n  MAX(CASE WHEN ID = 4 THEN ContactName END) AS ContactName4,\n  MAX(CASE WHEN ID = 4 THEN Designation END) AS Designation4\nFROM\n  YourTableName