CREATE PROCEDURE SearchCompanies\n    @CompanyType NVARCHAR(100) = NULL,\n    @CompanyName NVARCHAR(100) = NULL,\n    @Location NVARCHAR(100) = NULL\nAS\nBEGIN\n    SELECT \n        CompanyID,\n        CompanyName,\n        CompanyType,\n        Location\n    FROM \n        Companies\n    WHERE \n        (@CompanyType IS NULL OR CompanyType LIKE '%' + @CompanyType + '%')\n        AND (@CompanyName IS NULL OR CompanyName LIKE '%' + @CompanyName + '%')\n        AND (@Location IS NULL OR Location LIKE '%' + @Location + '%');\nEND