CREATE TABLE Surveys (\n    SurveyID NUMBER PRIMARY KEY,\n    SurveyName VARCHAR2(255) NOT NULL\n);\n\nCREATE TABLE Questions (\n    QuestionID NUMBER PRIMARY KEY,\n    SurveyID NUMBER,\n    QuestionText VARCHAR2(255) NOT NULL,\n    FOREIGN KEY (SurveyID) REFERENCES Surveys(SurveyID)\n);\n\nCREATE TABLE Answers (\n    AnswerID NUMBER PRIMARY KEY,\n    QuestionID NUMBER,\n    AnswerText VARCHAR2(255) NOT NULL,\n    FOREIGN KEY (QuestionID) REFERENCES Questions(QuestionID)\n);\n\nCREATE TABLE Responses (\n    ResponseID NUMBER PRIMARY KEY,\n    SurveyID NUMBER,\n    UserID NUMBER,\n    ResponseDate DATE,\n    FOREIGN KEY (SurveyID) REFERENCES Surveys(SurveyID)\n);\n\nCREATE TABLE ResponseDetails (\n    ResponseDetailID NUMBER PRIMARY KEY,\n    ResponseID NUMBER,\n    AnswerID NUMBER,\n    ResponseValue VARCHAR2(255),\n    FOREIGN KEY (ResponseID) REFERENCES Responses(ResponseID),\n    FOREIGN KEY (AnswerID) REFERENCES Answers(AnswerID)\n);