CREATE OR REPLACE PROCEDURE updateColumn(\n    pkey IN NUMBER,\n    columnName IN VARCHAR2,\n    newValue IN VARCHAR2\n) AS\n    sql_stmt VARCHAR2(4000);\nBEGIN\n    -- Construct the SQL statement dynamically\n    sql_stmt := 'UPDATE your_table SET ' || DBMS_ASSERT.SIMPLE_SQL_NAME(columnName) || ' = :1 WHERE PKEY = :2';\n\n    -- Execute the dynamic SQL statement\n    EXECUTE IMMEDIATE sql_stmt USING newValue, pkey;\nEND;\n/