CREATE OR REPLACE PROCEDURE GETCOMPANYANDORDERS (\n    CompanyCursor OUT SYS_REFCURSOR,\n    OrdersCursor OUT SYS_REFCURSOR\n)\nAS\nBEGIN\n    OPEN CompanyCursor FOR\n    SELECT COMPANYID, COMPANYNAME\n    FROM COMPANIES\n    WHERE IsActive = 'T';\n\n    OPEN OrdersCursor FOR\n    SELECT ORDERID, COMPANYID, ORDERDETAILS\n    FROM ORDERS\n    WHERE COMPANYID IN (SELECT COMPANYID FROM COMPANIES WHERE IsActive = 'T');\nEND GETCOMPANYANDORDERS;\n/