USE TestDB\nGO\n\nCREATE TRIGGER trgInsert\nON dbo.usage_reports_Dummy2\nINSTEAD OF INSERT\nAS \nBEGIN\n    INSERT INTO \n       dbo.usage_reports_Dummy2(member_id, login_time, logout_time, ip, session_id)\n          SELECT \n             member_id, login_time, logout_time, ip, session_id\n          FROM inserted \n          WHERE logout_time IS NOT NULL\n\n    INSERT INTO \n       dbo.usage_reports_Dummy2(member_id, login_time, logout_time, ip, session_id)\n          SELECT \n              member_id, \n              login_time, \n              DATEADD(SECOND, CAST(3600 * RAND() AS INT), login_time), \n              ip, \n              session_id\n          FROM inserted \n          WHERE logout_time IS NULL\nEND\nGO