CREATE OR REPLACE FUNCTION fetch_customer_id(customer_name VARCHAR2) RETURN NUMBER IS\n    customer_id NUMBER;\nBEGIN\n    SELECT customer_id\n      INTO customer_id\n      FROM CUSTOMERS\n     WHERE customer_name = customer_name;\n\n    RETURN customer_id;\nEXCEPTION\n    WHEN NO_DATA_FOUND THEN\n        RAISE_APPLICATION_ERROR(-20001, 'Customer named ' || customer_name || ' does not exist');\nEND;\n/